var fse = require('fs-extra');
var fs = require('fs');
var Path = require('path');

function emptyDir (path) {
	if(fs.existsSync(path)){
		var files = fs.readdirSync(path);
		for (var i = 0; i < files.length; i++) {
			var p = files[i];
			if('.git' === p) {
				continue;
			} else {
				fse.removeSync(Path.join(path, p));
			}
		};
	}
}

module.exports = emptyDir;